import React from "react";
import { render } from "react-dom";
import {createStore } from 'redux';
import {Provider} from 'react-redux';

import Counter from './counter';
import SomeContainer from './someContainer';

import "./index.css";

const initialState = { count: 0 };

class App extends React.Component
{
  reducer( state = initialState, action )
  {
    console.log( 'reducer', state, action );
    return state;
  }
  store = createStore( this.reducer );

  state = { count: 0 };

  increment = () =>
  {
      this.setState( { count: this.state.count + 1 } );
  }

  decrement = () =>
  {
      this.setState( { count: this.state.count - 1 } );
  }

  render()
  {
    return(
      <Provider store={this.store}>
        <div className="arbitrary-block">
            <Counter
              app_count={this.state.count}
              app_increment_function={this.increment}
              app_decrement_function={this.decrement} />
        </div>
        <div className="another-arbitrary-block">
          <SomeContainer
              count={this.state.count}
              increment={this.increment}
              decrement={this.decrement} />        
        </div>
    </Provider>  
    );
  }  
}

render(<App />, document.getElementById("root"));
