import React from 'react';
import "./counter.css";

class Counter extends React.Component
{

    render()
    {
        return(
            <div className="counter">
                <h2>Counter</h2>
                <div>
                    <button onClick={this.props.app_decrement_function}>-</button>
                    <span className="count">{this.props.app_count}</span>
                    <button onClick={this.props.app_increment_function}>+</button>
                </div>
            </div>
        )
    }
}

export default Counter;
